
CREATE TABLE IF NOT EXISTS syslog.rh_plaza (
    id INT UNSIGNED NOT NULL AUTO_INCREMENT,
    clave VARCHAR(30) NOT NULL,
    descr VARCHAR(55),
    tipo ENUM('DEFINITIVA', 'TEMPORAL') NOT NULL DEFAULT 'TEMPORAL',
    clave_puesto INT UNSIGNED NOT NULL,
    activa TINYINT NOT NULL DEFAULT 1,
    visible TINYINT NOT NULL DEFAULT 1,
    depto INT UNSIGNED,
    clave_descanso CHAR(2) NOT NULL,
    clave_jornada INT UNSIGNED NOT NULL,
    fec_creacion DATE DEFAULT NULL,
    residencia VARCHAR(60) DEFAULT NULL,
    localidad VARCHAR(60) DEFAULT NULL,
    taller VARCHAR(60) DEFAULT NULL,
    instalacion VARCHAR(60) DEFAULT NULL,
    funcion VARCHAR(60) DEFAULT NULL,
    grupo VARCHAR(40) DEFAULT NULL,
    sirhn INT UNSIGNED,
    posfin INTEGER UNSIGNED,
    PRIMARY KEY(id),
    INDEX IDX_clave_plaza(clave ASC),

    INDEX IDX_clave_descanso(clave_descanso ASC),
    CONSTRAINT FK_clave_descanso FOREIGN KEY(clave_descanso) REFERENCES syslog.rh_descanso(clave)
        ON DELETE CASCADE
        ON UPDATE NO ACTION,

    INDEX IDX_clave_jornada(clave_jornada ASC),
    CONSTRAINT FK_clave_jornada FOREIGN KEY(clave_jornada) REFERENCES syslog.rh_jornada(clave) 
        ON DELETE CASCADE 
        ON UPDATE NO ACTION,

    INDEX IDX_clave_puesto(clave_puesto ASC),
    CONSTRAINT FK_clave_puesto FOREIGN KEY(clave_puesto) REFERENCES syslog.rh_puesto(clave)
        ON DELETE CASCADE
        ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ci;




