CREATE TABLE IF NOT EXISTS syslog.rh_ausencia (
	id INT UNSIGNED NOT NULL AUTO_INCREMENT, 
	clave_trab INT UNSIGNED NOT NULL, 
	id_ausencia INT UNSIGNED NOT NULL,
	clave_plaza VARCHAR(20) NOT NULL, 
	clave_tipo CHAR(3) NOT NULL, 
	fec_inicio DATE NOT NULL, 
	fec_termino DATE NOT NULL, 
	fec_reanuda DATE  NULL, 
	req_cobertura TINYINT(1) NOT NULL DEFAULT 1, 
	docs TEXT NULL, 
	obs TEXT NULL, 
	PRIMARY KEY(id), 
	INDEX IDX_clave_trab(clave_trab ASC), 
	CONSTRAINT FK_clave_trab FOREIGN KEY clave_trab REFERENCES syslog.rh_trab(clave) 
		ON DELETE CASCADE ON UPDATE NO ACTION, 
	CONSTRAINT FK_clave_plaza FOREIGN KEY clave_plaza REFERENCES syslog.rh_plaza(clave) 
		ON DELETE CASCADE ON UPDATE NO ACTION, 
	CONSTRAINT FK_clave_tipo FOREIGN KEY clave_tipo REFERENCES syslog.rh_ausencia_tipo(clave) 
		ON DELETE CASCADE ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARACTER SET=utf8 COLLATE=utf8_spanish_ci;

